sp_widget.client_script:
api.controller=function() {
  /* widget controller */
  var c = this;
	if (c.data.category !== undefined && c.data.topic_id !== undefined) {
		c.data.href = "/esc?id="+ c.data.category +"&topic_id="+ c.data.topic_id;
	}
};

